import React from 'react';
import { motion } from 'framer-motion';

interface User {
  username: string;
}

interface HomePageProps {
  user: User | null;
  setPage: (pageName: string) => void;
  handleLogout: () => void;
}

export default function HomePage({ user, setPage, handleLogout }: HomePageProps) {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-100 to-pink-100 text-center p-8"
    >
      <h1 className="text-5xl font-bold mb-6">Welcome, {user?.username}!</h1>
      <p className="text-lg text-gray-600 mb-8 max-w-xl">
        This is the home page of <strong>Just Because</strong> â€” a place to share thoughts, just because.
      </p>
      <div className="space-x-4">
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => setPage('survey')}
          className="bg-pink-500 hover:bg-pink-600 text-white px-6 py-3 rounded-2xl shadow-lg transition"
        >
          Take Survey
        </motion.button>
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleLogout}
          className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-6 py-3 rounded-2xl shadow-lg transition"
        >
          Logout
        </motion.button>
      </div>
    </motion.div>
  );
}
