import React, { useState } from 'react';

interface SurveyEntry {
  id: number;
  feeling: string;
  smile: string;
  message?: string;
}

interface SurveyPageProps {
  handleSurveySubmit: (e: React.FormEvent<HTMLFormElement>, setNotification: (message: string) => void) => void;
  surveyData: SurveyEntry[];
  setPage: (pageName: string) => void;
}

// Helper function to sanitize input to prevent XSS
const sanitizeInput = (input: string) => {
  const div = document.createElement('div');
  div.appendChild(document.createTextNode(input));
  return div.innerHTML;
};

export default function SurveyPage({ handleSurveySubmit, surveyData, setPage }: SurveyPageProps) {
  const [notification, setNotification] = useState('');

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    handleSurveySubmit(e, setNotification);
    (e.target as HTMLFormElement).reset();
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-green-100 to-blue-100 p-8">
      <div className="bg-white shadow-lg rounded-2xl p-8 w-full max-w-lg">
        <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">Quick Survey</h2>
        <form onSubmit={onSubmit} className="flex flex-col space-y-4">
          <label className="flex flex-col">
            <span className="mb-1 font-medium">How are you feeling today?</span>
            <div className="flex flex-wrap gap-4 mt-2">
              <label className="inline-flex items-center">
                <input type="radio" name="feeling" value="Happy" className="form-radio text-blue-600" required />
                <span className="ml-2">Happy</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="feeling" value="Inspired" className="form-radio text-purple-600" required />
                <span className="ml-2">Inspired</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="feeling" value="Neutral" className="form-radio text-gray-600" required />
                <span className="ml-2">Neutral</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="feeling" value="Tired" className="form-radio text-red-600" required />
                <span className="ml-2">Tired</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="feeling" value="Anxious" className="form-radio text-yellow-600" required />
                <span className="ml-2">Anxious</span>
              </label>
            </div>
          </label>
          <label className="flex flex-col">
            <span className="mb-1 font-medium">What's one thing that made you smile?</span>
            <div className="flex flex-wrap gap-4 mt-2">
              <label className="inline-flex items-center">
                <input type="radio" name="smile" value="A good joke" className="form-radio text-blue-600" required />
                <span className="ml-2">A good joke</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="smile" value="Time with loved ones" className="form-radio text-purple-600" required />
                <span className="ml-2">Time with loved ones</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="smile" value="Achieving a goal" className="form-radio text-gray-600" required />
                <span className="ml-2">Achieving a goal</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="smile" value="Nature's beauty" className="form-radio text-green-600" required />
                <span className="ml-2">Nature's beauty</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="smile" value="Something unexpected" className="form-radio text-yellow-600" required />
                <span className="ml-2">Something unexpected</span>
              </label>
            </div>
          </label>
          <label className="flex flex-col">
            <span className="mb-1 font-medium">Anything you'd like to share?</span>
            <div className="flex flex-wrap gap-4 mt-2">
              <label className="inline-flex items-center">
                <input type="radio" name="message" value="Just a thought" className="form-radio text-blue-600" required />
                <span className="ml-2">Just a thought</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="message" value="A suggestion" className="form-radio text-purple-600" required />
                <span className="ml-2">A suggestion</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="message" value="A question" className="form-radio text-gray-600" required />
                <span className="ml-2">A question</span>
              </label>
              <label className="inline-flex items-center">
                <input type="radio" name="message" value="No, thanks" className="form-radio text-red-600" required />
                <span className="ml-2">No, thanks</span>
              </label>
            </div>
          </label>
          <button type="submit" className="bg-blue-500 hover:bg-blue-600 text-white py-2 rounded-lg font-semibold transition">Submit</button>
        </form>
        {notification && (
          <div className="mt-4 p-3 bg-green-100 text-green-700 rounded-lg text-center">
            {notification}
          </div>
        )}
        <button onClick={() => setPage('home')} className="mt-4 text-blue-600 underline">Back to Home</button>
      </div>

      {surveyData.length > 0 && (
        <div className="mt-8 bg-white rounded-xl p-6 shadow-md w-full max-w-lg">
          <h3 className="text-xl font-semibold mb-4 text-gray-800">Survey Data</h3>
          <ul className="space-y-2 text-gray-600">
            {surveyData.map((entry) => (
              <li key={entry.id} className="border-b pb-2">
                <strong>Feeling:</strong> {sanitizeInput(entry.feeling)} | <strong>Smile:</strong> {sanitizeInput(entry.smile)} | <strong>Message:</strong> {sanitizeInput(entry.message || 'N/A')}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
