import React from 'react';
import { motion } from 'framer-motion';

interface WelcomePageProps {
  setPage: (pageName: string) => void;
}

export default function WelcomePage({ setPage }: WelcomePageProps) {
  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-100 to-pink-100 text-center p-8"
    >
      <img src="/logo.png" alt="Main Logo" className="w-64 h-64 object-contain mb-8" />
      <h3 className="text-5xl font-bold text-gray-800 mb-6"><i>Love needs no reason</i></h3>
      <p className="text-lg text-gray-600 mb-8 max-w-xl">
        Discover and share heartfelt moments.
      </p>
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => setPage('login')}
        className="bg-pink-500 hover:bg-pink-600 text-white px-6 py-3 rounded-2xl shadow-lg transition"
      >
        Take Survey
      </motion.button>
    </motion.div>
  );
}
