import React, { useState, useEffect } from 'react';

interface WelcomePageProps {
  setPage: (pageName: string) => void;
}

const giftImages = [
  'https://via.placeholder.com/150/FF0000/FFFFFF?text=Gift1',
  'https://via.placeholder.com/150/00FF00/FFFFFF?text=Gift2',
  'https://via.placeholder.com/150/0000FF/FFFFFF?text=Gift3',
];

const logoImages = [
  'https://via.placeholder.com/100/FFFF00/000000?text=Logo1',
  'https://via.placeholder.com/100/00FFFF/000000?text=Logo2',
  'https://via.placeholder.com/100/FF00FF/000000?text=Logo3',
];

export default function WelcomePage({ setPage }: WelcomePageProps) {
  const [currentGiftIndex, setCurrentGiftIndex] = useState(0);
  const [currentLogoIndex, setCurrentLogoIndex] = useState(0);

  useEffect(() => {
    const giftInterval = setInterval(() => {
      setCurrentGiftIndex((prevIndex) => (prevIndex + 1) % giftImages.length);
    }, 2000); // Change gift image every 2 seconds

    const logoInterval = setInterval(() => {
      setCurrentLogoIndex((prevIndex) => (prevIndex + 1) % logoImages.length);
    }, 3000); // Change logo image every 3 seconds

    const timer = setTimeout(() => {
      setPage('login');
    }, 5000); // Transition to login page after 5 seconds

    return () => {
      clearInterval(giftInterval);
      clearInterval(logoInterval);
      clearTimeout(timer);
    };
  }, [setPage]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-4xl font-bold text-gray-800 mb-8">Welcome!</h1>
      <div className="flex space-x-8 mb-8">
        <img src={giftImages[currentGiftIndex]} alt="Gift" className="w-48 h-48 object-contain" />
        <img src={logoImages[currentLogoIndex]} alt="Logo" className="w-32 h-32 object-contain" />
      </div>
      <p className="text-lg text-gray-600">Prepare for an amazing experience...</p>
    </div>
  );
}
